    keywordsAr: ['كتف', 'تمرين الكتف', 'ديلتس', 'كتف كبير', 'يوم الكتف', 'لاترال ريز', 'بريس علوي', 'كتف أمامي', 'كتف جانبي', 'كتف خلفي', 'كتف عريض'],
